(3, 13, 'WooCommerce', '', '', '', '2017-03-02 08:20:55', '2017-03-02 08:20:55', 'Payment to be made upon delivery. Order status changed from Pending Payment to Processing.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(4, 19, 'WooCommerce', '', '', '', '2017-03-02 10:38:29', '2017-03-02 10:38:29', 'Booking #18 status changed from "in-cart" to "unpaid"', 0, '1', 'WooCommerce', 'order_note', 0, 0),
